const webhookData = $json.body || {};  // Datos del Webhook
const setData = $json || {};           // Datos del nodo Set

// Detectar la propiedad tipo "json0", "json1", etc.
const dynamicKeyWebhook = Object.keys(webhookData).find(k => /^json\d+$/.test(k));
const dynamicKeySet = Object.keys(setData).find(k => /^json\d+$/.test(k));

// Seleccionar los datos vÃ¡lidos
const dataAtm = 
    (dynamicKeyWebhook && webhookData[dynamicKeyWebhook]) || 
    (dynamicKeySet && setData[dynamicKeySet]) || 
    null;

// Resultado final
const finalData = { dataAtm };

return [{ json: finalData }];

// Entrada: La salida del Agente de IA
const rawOutput = $input.first().json.output; // Accede al campo "output"

// Extraer el contenido JSON eliminando el Markdown (```json y ```)
const jsonString = rawOutput
  .replace("```json", "") // Elimina el inicio del bloque Markdown
  .replace("```", "")     // Elimina el final del bloque Markdown
  .trim();                // Elimina espacios en blanco adicionales

// Parsear la cadena JSON a un objeto
const parsedJson = JSON.parse(jsonString);

// Salida: Devolver el JSON limpio
return [{
  json: parsedJson
}];

// Obtener el primer (y Ãºnico) item del input
