// Construir el nuevo objeto con las claves TYPE#
const result = {
  TYPE1: item.gav1,
  TYPE2: item.gav2,
  TYPE3: item.gav3,
  TYPE4: item.gav4,
};

// Retornar como salida del nodo
return [{ data: result }];

const items = $input.all();

return items.map(item => {
  const key = Object.keys(item.json).find(k => k !== '_id');
  return {
    json: {
      dataAtm: item.json[key]
    }
  };
});

